import React from 'react';
const item = [
    { ID: 1, name: 'Reading books always makes the moments happy', data:'2 aug, 2021',img:'../../../img/blog/blog.png' },
    { ID: 1, name: 'Reading books always makes ', data:'2 aug, 2022' },
    { ID: 1, name: 'Reading books ', data:'2 aug, 2023' },
                
  ]
  

const BlogItem = () => {
    let ass = item.map((item) =>{
        if(item.name)
        return <p className="item_blog_name">{item.name}</p>
    })
    return (
        <div>
            <img src={item.img} alt="" />
            <p className="item_data">{ass}</p>
            <p className="item_blog_name">{item.name}</p>
            <hr className="item_line" />
            <button className="item_btn">Читати &#8594;</button>
        </div>
    );
};

export default BlogItem;
