import React, { useState } from "react";
import list from "./katalogData";
import Item from "./Item";

const KatalogItem = ({ handle }) => {
  const [value, setValue] = useState("");
  const [sortType, setSortType] = useState("asc"); // додатковий стан для збереження типу сортування
  const filteredList = list.filter((item) =>
    item.name.toLowerCase().includes(value.toLowerCase())
  );

  // функція для відсортування
  const sortList = (list) => {
    const sortedList = [...list];
    return sortedList.sort((a, b) => {
      if (sortType === "asc") {
        return a.price - b.price;
      } else {
        return b.price - a.price;
      }
    });
  };

  const sortedList = sortList(filteredList);

  return (
    <div>
      <div className="input_sort_line">
        <input
          placeholder="Знайти..."
          type="text"
          onChange={(event) => setValue(event.target.value)}
        />
        <div className="sort_container">
          <p className="sort_name">Сортувати за :</p>
          <button onClick={() => setSortType("asc")}>За ціною +</button>
          <button onClick={() => setSortType("desc")}>За ціною -</button>
          <button onClick={() => setSortType(null)}>Скасувати сортування</button>
        </div>
      </div>

      {sortedList.length === 0 ? (
        <p>Немає товарів, які відповідали б пошуку</p>
      ) : (
        <div className="katalog_container_blok">
          {sortedList.map((item) => (
            <Item key={item.id} item={item} handle={handle} />
          ))}
        </div>
      )}
    </div>
  );
};

export default KatalogItem;
