import React, { useState } from 'react';
import itemImg from '../../../img/blog/item.png'
import itemImg2 from '../../../img/blog/item2.png'
import itemImg3 from '../../../img/blog/item3.png'
import { NavLink } from 'react-router-dom';
import Item from './Item';
const item = [
    { ID: 1, name: 'Reading books always makes the moments happy', data:'2 aug, 2021',img:itemImg},
    { ID: 2, name: 'Reading books always makes the moments happy ', data:'2 aug, 2022',img:itemImg2 },
    { ID: 3, name: 'Reading books always makes the moments happy ', data:'2 aug, 2023',img:itemImg3 },
    { ID: 4, name: 'Reading books ', data:'2 aug, 2023',img:itemImg },
    { ID: 5, name: 'Reading books ', data:'2 aug, 2023',img:itemImg2 },
    { ID: 6, name: 'Reading books ', data:'2 aug, 2023',img:itemImg3 },
                
  ]
  

const BlogItem = (props) => {
    let items = item.map((item) =>{
    const [name,setName]= useState('')
    const itemClick = (event) =>{
        setName(event.target.value)
    }
        return (
            
       
       <div className='items_blok' >
       <img src={item.img} alt="" />
       <p className='item_blog_data'>{item.data}</p>
       <p className='item_blog_name'>{item.name}</p>
       <hr className="item_line" />
       <NavLink   to="/item"  > <button onClick={itemClick} className="item_btn">Читати &#8594;</button></NavLink>
        <Item item={item.name} />
       </div>
       
        )
    })
    return (
        <div className='item_container_blok'>
            {items}
          
            
        </div>
    );
};

export default BlogItem;
