import React, { useState } from 'react';
import list from './katalogData';
import Item from './Item';

const KatalogItem = ({ handle }) => {
  const [value, setValue] = useState('');
  const [sortOrder, setSortOrder] = useState(null);

  const filterSearch = list.filter((list) => {
    return list.name.toLowerCase().includes(value.toLowerCase());
  });

  const sortByPrice = () => {
    const numAscending = [...filterSearch].sort((a, b) => a.price - b.price);
    setSortOrder(numAscending);
  };

  const sortByName = () => {
    const nameAscending = [...filterSearch].sort((a, b) =>
      a.name.localeCompare(b.name)
    );
    setSortOrder(nameAscending);
  };

  const sortedArray = sortOrder ? sortOrder : filterSearch;

  return (
    <div>
      <div className="input_sort_line">
        <input
          placeholder="Знайти..."
          type="text"
          onChange={(event) => setValue(event.target.value)}
        />
        <div className="sort_container">
          <p className="sort_name">Сортувати за :</p>
          <button onClick={sortByPrice}>За ціною +</button>
          <button onClick={() => sortByPrice().reverse()}>За ціною -</button>
          <button onClick={sortByName}>За алфавітом</button>
        </div>
      </div>

      <div className="katalog_container_blok">
        {sortedArray.length ? (
          sortedArray.map((item) => (
            <Item key={item.id} item={item} handle={handle} />
          ))
        ) : (
          <p>Нічого не знайдено</p>
        )}
      </div>
    </div>
  );
};

export default KatalogItem;
