import React, { useState } from 'react';
import list from './katalogData';
import Item from './Item';

const KatalogItem = ({ handle }) => {
  const [value, setValue] = useState('');
  const [sortedArray, setSortedArray] = useState(null);

  const filterSearch = list.filter((item) => {
    return item.name.toLowerCase().includes(value.toLowerCase());
  });

  const sortAscending = () => {
    const numAscending = [...filterSearch].sort((a, b) => a.price - b.price);
    setSortedArray(numAscending);
  };

  const sortDescending = () => {
    const numDescending = [...filterSearch].sort((a, b) => b.price - a.price);
    setSortedArray(numDescending);
  };

  const sortAlphabetically = () => {
    const alphaSort = [...filterSearch].sort((a, b) =>
      a.name.localeCompare(b.name)
    );
    setSortedArray(alphaSort);
  };

  const handleSortClick = (sortFunction) => {
    sortFunction();
  };

  return (
    <div>
      <div className="input_sort_line">
        <input
          placeholder="Знайти..."
          type="text"
          onChange={(event) => setValue(event.target.value)}
        />
        <div className="sort_container">
          <p className="sort_name">Сортувати за :</p>
          <button onClick={() => handleSortClick(sortAscending)}>
            За ціною +
          </button>
          <button onClick={() => handleSortClick(sortDescending)}>
            За ціною -
          </button>
          <button onClick={() => handleSortClick(sortAlphabetically)}>
            За алфавітом
          </button>
        </div>
      </div>

      <div className="katalog_container_blok">
        {sortedArray
          ? sortedArray.map((item) => (
              <Item key={item.id} item={item} handle={handle} />
            ))
          : filterSearch.length > 0
          ? filterSearch.map((item) => (
              <Item key={item.id} item={item} handle={handle} />
            ))
          : 'Такого об\'єкту немає'}
      </div>
    </div>
  );
};

export default KatalogItem;
